INSERT INTO `content` (`id`, `title`, `editor`, `type`, `alias`, `summary`, `body`, `picture`, `template`, `status`, `categoryId`, `tagIds`, `hits`, `featured`, `promoted`, `sticky`, `userId`, `field1`, `field2`, `field3`, `field4`, `field5`, `field6`, `field7`, `field8`, `field9`, `field10`, `publishedTime`, `createdTime`) VALUES (1,'关于我们','richeditor','page','aboutus',NULL,'<p><img alt=\"\" src=\"http://edusoho-demo.b0.upaiyun.com/files/default/2015/06-11/1756524ac51a987008.jpg?6.0.2\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>杭州阔知网络科技有限公司是中国领先的在线教育基础服务提供商，专注于在线教育，截止2015年5月19日已有1万3千多家网校在使用edusoho搭建网校，目前<strong>网校数量行业第一，客户规模行业第一，中国首个开源网络课堂-EduSoho通用版。中国首个开源的慕课平台-edusoho慕课版。</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>推出中国第一个教育行业专属教育云：</strong>云视频、云直播、云文档、云短信，独创TLP2.0安全技术，最高性价比方案（比其他云再省百分50成本），最高安全技术（万家网校0事故），最简集成（一键集成）。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>阔知网络成立于2012年5月，创始团队拥有海内外的教育和工作背景。公司坚持以创新为企业发展动力，以共赢共生为合作理念，为加速中国在线教育发展，创造全新教育方式做出贡献。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>EduSoho官网：<u><a href=\"http://www.edusoho.com/\">http://www.edusoho.com/</a></u></strong></p>\r\n\r\n<p><strong>EduSoho网络课堂演示站：<u><a href=\"http://demo.edusoho.com/\">http://demo.edusoho.com/</a></u></strong></p>\r\n\r\n<p><strong>EduSoho网络课堂使用学习站（气球鱼学院）：<u><a href=\"http://www.qiqiuyu.com/\">http://www.qiqiuyu.com/</a></u></strong></p>\r\n','','default','published',0,NULL,0,0,0,0,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1429260383,1429260383),(2,'帮助','richeditor','page','help',NULL,'<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\">EduSoho除了提供领先的在线教育平台产品，还从更多方面帮助用户做在线教育：</span></p>\r\n\r\n<p><strong>产品及服务介绍：</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; EduSoho官网:<u><a href=\"http://www.edusoho.com/\">http://www.edusoho.com/</a></u></strong></p>\r\n\r\n<p><strong>课程制作及维护</strong></p>\r\n\r\n<p>&nbsp; &nbsp; 微课及慕课录制及制作，请与销售联系。</p>\r\n\r\n<p><strong>在线教育行业资讯</strong></p>\r\n\r\n<p><strong>&nbsp; <u>&nbsp;<a href=\"http://www.qiqiuyu.com/article/category/news\">&nbsp;http://www.qiqiuyu.com/article/category/news</a></u></strong></p>\r\n\r\n<p><strong>网校运营知识</strong></p>\r\n\r\n<p>&nbsp; &nbsp; 气球鱼学院网校运营课程:<strong><u><a href=\"http://www.qiqiuyu.com/course/explore/operation\">http://www.qiqiuyu.com/course/explore/operation</a></u></strong></p>\r\n\r\n<p><strong>系统二次开发与开发者</strong></p>\r\n\r\n<ul>\r\n	<li>EduSoho开放平台官网：<strong><u><a href=\"http://open.edusoho.com/\">http://open.edusoho.com/</a></u></strong></li>\r\n	<li>开发者社区：<a href=\"http://www.qiqiuyu.com/group/5\"><u><strong>http://www.qiqiuyu.com/group/5</strong></u></a></li>\r\n	<li>开发课程：<u><strong><a href=\"http://www.qiqiuyu.com/course/explore/developer\">http://www.qiqiuyu.com/course/explore/developer</a></strong></u></li>\r\n</ul>\r\n\r\n<div><strong>EduSoho使用</strong></div>\r\n\r\n<div>&nbsp; &nbsp; 气球鱼学院使用课程:<strong><a href=\"http://www.qiqiuyu.com/course/explore/developer\">http://www.qiqiuyu.com/course/explore/fuction</a></strong></div>\r\n\r\n<div><strong>EduSoho反馈社区</strong></div>\r\n\r\n<div><strong>&nbsp; &nbsp;&nbsp;</strong>给ES提建议小组:<strong><a href=\"http://www.qiqiuyu.com/course/explore/developer\">http://www.qiqiuyu.com/group/4</a></strong></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>关注EduSoho微信，了解最新的产品及活动消息！</div>\r\n\r\n<p>&nbsp;</p>\r\n','','default','published',0,NULL,0,0,0,0,1,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1429260383,1429260383);
