#{weatherDate,jdbcType=DATE}, #{pm25,jdbcType=VARCHAR}, #{dayPictureUrl,jdbcType=VARCHAR},
#{nightPictureUrl,jdbcType=VARCHAR}, #{weather,jdbcType=VARCHAR}, #{wind,jdbcType=VARCHAR},
#{temperature,jdbcType=VARCHAR}, #{realTemp,jdbcType=VARCHAR}, #{weatherIndex,jdbcType=VARCHAR}
