mapper.select:
select

from feed_comment
where 1 = 1

	and fid = #{fid,jdbcType=NUMERIC}


	and user_id = #{userId,jdbcType=NUMERIC}


	and feed_type = #{feed_type,jdbcType=NUMERIC}


	and status = #{status,jdbcType=NUMERIC}

order by add_time desc

