mapper.select:
select

from feeds
where 1=1

	and fid = #{fid,jdbcType=NUMERIC}


	and user_id = #{userId,jdbcType=NUMERIC}



	and ( user_id = #{commentUserId,jdbcType=NUMERIC} OR fid IN (select fid from feed_comment where user_id = #{commentUserId,jdbcType=NUMERIC} and feed_type = #{feedType,jdbcType=NUMERIC})  )



	and feed_type = #{feedType,jdbcType=NUMERIC}



	and user_id in

		#{item}






	and status = #{status,jdbcType=NUMERIC}


order by add_time desc

