select user_id, count(distinct fid) as total from ( select user_id, fid from feeds where 1 = 1
and user_id in
#{item}
union all select user_id, fid from feed_comment where 1 = 1 and user_id in
#{item}
) as T group by user_id

mapper.update:
update feeds
set total_view = total_view + 1
where fid = #{fid,jdbcType=NUMERIC}
