mapper.select:
	select
	 
	from msg where 1=1
	
		 	and user_type = #{userType}
    
	and msg_id not in (
		 SELECT 
		 msg_id from msg_readed
		 where 1=1
		 
		 	and user_id = #{userId}
		 
		
	)	
	 
		 	and title = #{title,jdbcType=VARCHAR}
		 
	
		 
			and is_send = #{isSend,jdbcType=NUMERIC}
		 
		 
		 
			and user_type = #{userType,jdbcType=NUMERIC}
		 
	order by add_time desc
