mapper.select:
select

from hr_resume_change
where 1 = 1 

	and user_id = #{userId,jdbcType=NUMERIC}



	and limit_day = #{limitDay,jdbcType=NUMERIC}


order by update_time desc

select
from record_assets where 1 = 1 from hr_resume_change where 1 = 1
and user_id = #{userId,jdbcType=NUMERIC}
and limit_day = #{limitDay,jdbcType=NUMERIC}
order by update_time desc
