mapper.select:
select

from user_msg
where 1=1

	and from_user_id = #{fromUserId,jdbcType=NUMERIC}



	and to_user_id = #{toUserId,jdbcType=NUMERIC}



	and category = #{category,jdbcType=VARCHAR}



	and action = #{action,jdbcType=VARCHAR}



	and params = #{params,jdbcType=VARCHAR}



	 and ( (add_time >= #{startTime,jdbcType=NUMERIC} and add_time <= #{endTime,jdbcType=NUMERIC}) OR (service_time >= #{serviceTime,jdbcType=NUMERIC} and service_time <= #{serviceTime,jdbcType=NUMERIC}) ) 


order by msg_id desc
