mapper.select:
select

from user_ref
where 1=1

	and id = #{id,jdbcType=NUMERIC}



	and ref_id = #{refId,jdbcType=NUMERIC}



	and ref_type = #{refType,jdbcType=VARCHAR}



	 and  (add_time >= #{startTime,jdbcType=NUMERIC} and add_time <= #{endTime,jdbcType=NUMERIC})   

order by id desc

select

from user_ref
where 1=1

	and id = #{id,jdbcType=NUMERIC}



	and ref_id = #{refId,jdbcType=NUMERIC}



	and ref_type = #{refType,jdbcType=VARCHAR}



