mapper.select:
select

from xcompany_admin
where 1 = 1

	and company_id = #{companyId,jdbcType=NUMERIC}



	and company_name = #{companyName,jdbcType=VARCHAR}



	and userId = #{userId,jdbcType=NUMERIC}



	and user_name = #{userName,jdbcType=VARCHAR}



	and password = #{passMd5,jdbcType=VARCHAR}




	and is_create = #{isCreate,jdbcType=NUMERIC}



	 and add_time >= #{startTime,jdbcType=NUMERIC} and add_time <= #{endTime,jdbcType=NUMERIC}

