mapper.select:
select

from xcompany_assets
where 1 = 1 

	and asset_id = #{assetId,jdbcType=NUMERIC}



	and company_id = #{companyId,jdbcType=NUMERIC}



	and asset_type_id = #{assetTypeId,jdbcType=NUMERIC}



	and name = #{name,jdbcType=VARCHAR}


order by update_time desc

